const newsletterFeed = require(`./src/utils/newsletterFeed`)

module.exports = (options) => {
  const { feed = true, feedTitle = `Ayan Bag` } = options

  return {
    siteMetadata: {
      siteTitle: `Ayan Bag`,
      siteTitleAlt: `Ayan Bag`,
      siteHeadline: `Ayan Bag`,
      siteUrl: `https://ayanbag.com`,
      siteDescription: `I'm a Back-end Developer creating open source projects and writing about modern JavaScript, Python, and development.`,
      siteLanguage: `en`,
      siteImage: `/banner.jpg`,
      author: `Ayan Bag`,
    },
    plugins: [
      {
        resolve: `@lekoarts/gatsby-theme-minimal-blog-core`,
        options,
      },
      feed && {
        resolve: `gatsby-plugin-feed`,
        options: newsletterFeed(feedTitle),
      },
      `gatsby-plugin-react-helmet`,
      `gatsby-plugin-typescript`,
      `gatsby-plugin-catch-links`,
      `gatsby-plugin-theme-ui`,
    ].filter(Boolean),
  }
}
